import { useState } from 'react';
import { useQuery } from '@tanstack/react-query';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { Badge } from '@/components/ui/badge';
import { Button } from '@/components/ui/button';
import { Progress } from '@/components/ui/progress';
import { ScrollArea } from '@/components/ui/scroll-area';
import { Separator } from '@/components/ui/separator';
import { 
  FolderOpen, 
  Calendar, 
  FileText, 
  Download,
  CheckCircle,
  Clock,
  AlertCircle,
  DollarSign
} from 'lucide-react';
import { format } from 'date-fns';

interface Project {
  id: string;
  title: string;
  description: string;
  status: string;
  progress: number;
  budget?: number;
  deadline?: string;
  milestones: {
    id: string;
    title: string;
    completed: boolean;
    dueDate?: string;
  }[];
  attachments: {
    id: string;
    name: string;
    url: string;
    type: string;
  }[];
}

export default function ClientPortal() {
  const [activeTab, setActiveTab] = useState('projects');

  // Mock authentication check - in real app this would be handled by auth system
  const isAuthenticated = true; // This would come from your auth context

  const { data: projects, isLoading } = useQuery<Project[]>({
    queryKey: ['/api/client/projects'],
    enabled: isAuthenticated
  });

  if (!isAuthenticated) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gray-50">
        <Card className="w-96">
          <CardHeader>
            <CardTitle>Client Portal Access</CardTitle>
          </CardHeader>
          <CardContent>
            <p className="text-center text-muted-foreground mb-4">
              Please log in to access your client portal
            </p>
            <Button className="w-full" data-testid="button-login">
              Log In
            </Button>
          </CardContent>
        </Card>
      </div>
    );
  }

  if (isLoading) {
    return (
      <div className="min-h-screen bg-gray-50 p-6">
        <div className="max-w-6xl mx-auto">
          <div className="text-center py-12">Loading your projects...</div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50 p-6">
      <div className="max-w-6xl mx-auto">
        <div className="mb-8">
          <h1 className="text-3xl font-bold text-gray-900">Client Portal</h1>
          <p className="text-gray-600 mt-2">Track your projects and collaborate with our team</p>
        </div>

        <Tabs value={activeTab} onValueChange={setActiveTab} className="space-y-6">
          <TabsList>
            <TabsTrigger value="projects" className="flex items-center gap-2">
              <FolderOpen className="w-4 h-4" />
              My Projects
            </TabsTrigger>
            <TabsTrigger value="documents" className="flex items-center gap-2">
              <FileText className="w-4 h-4" />
              Documents
            </TabsTrigger>
            <TabsTrigger value="calendar" className="flex items-center gap-2">
              <Calendar className="w-4 h-4" />
              Schedule
            </TabsTrigger>
          </TabsList>

          <TabsContent value="projects" className="space-y-6">
            <div className="grid gap-6">
              {projects?.map((project: Project) => (
                <ProjectCard key={project.id} project={project} />
              ))}
            </div>
          </TabsContent>

          <TabsContent value="documents" className="space-y-6">
            <DocumentsSection projects={projects || []} />
          </TabsContent>

          <TabsContent value="calendar" className="space-y-6">
            <CalendarSection projects={projects || []} />
          </TabsContent>
        </Tabs>
      </div>
    </div>
  );
}

function ProjectCard({ project }: { project: Project }) {
  const [expanded, setExpanded] = useState(false);

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'completed': return 'bg-green-100 text-green-800';
      case 'in_progress': return 'bg-blue-100 text-blue-800';
      case 'review': return 'bg-yellow-100 text-yellow-800';
      case 'planning': return 'bg-gray-100 text-gray-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  const getStatusIcon = (status: string) => {
    switch (status) {
      case 'completed': return <CheckCircle className="w-4 h-4" />;
      case 'in_progress': return <Clock className="w-4 h-4" />;
      case 'review': return <AlertCircle className="w-4 h-4" />;
      default: return <FolderOpen className="w-4 h-4" />;
    }
  };

  return (
    <Card>
      <CardHeader>
        <div className="flex items-start justify-between">
          <div className="space-y-2">
            <CardTitle className="flex items-center gap-2">
              {project.title}
              {getStatusIcon(project.status)}
            </CardTitle>
            <p className="text-muted-foreground">{project.description}</p>
          </div>
          <div className="flex items-center gap-2">
            <Badge className={getStatusColor(project.status)}>
              {project.status.replace('_', ' ')}
            </Badge>
            {project.budget && (
              <Badge variant="outline" className="flex items-center gap-1">
                <DollarSign className="w-3 h-3" />
                {project.budget.toLocaleString()}
              </Badge>
            )}
          </div>
        </div>
      </CardHeader>

      <CardContent className="space-y-4">
        <div className="space-y-2">
          <div className="flex justify-between text-sm">
            <span>Progress</span>
            <span>{project.progress}%</span>
          </div>
          <Progress value={project.progress} className="h-2" />
        </div>

        {project.deadline && (
          <div className="flex items-center gap-2 text-sm text-muted-foreground">
            <Calendar className="w-4 h-4" />
            Deadline: {format(new Date(project.deadline), 'PPP')}
          </div>
        )}

        <div className="flex items-center justify-between">
          <div className="text-sm text-muted-foreground">
            {project.milestones.filter(m => m.completed).length} of {project.milestones.length} milestones completed
          </div>
          <Button 
            variant="outline" 
            size="sm" 
            onClick={() => setExpanded(!expanded)}
            data-testid={`button-expand-project-${project.id}`}
          >
            {expanded ? 'Less Details' : 'View Details'}
          </Button>
        </div>

        {expanded && (
          <div className="mt-4 space-y-4 border-t pt-4">
            <div>
              <h4 className="font-semibold mb-2">Milestones</h4>
              <div className="space-y-2">
                {project.milestones.map((milestone) => (
                  <div key={milestone.id} className="flex items-center gap-2 text-sm">
                    <CheckCircle 
                      className={`w-4 h-4 ${milestone.completed ? 'text-green-600' : 'text-gray-300'}`}
                    />
                    <span className={milestone.completed ? 'line-through text-muted-foreground' : ''}>
                      {milestone.title}
                    </span>
                    {milestone.dueDate && (
                      <span className="text-muted-foreground ml-auto">
                        Due: {format(new Date(milestone.dueDate), 'PP')}
                      </span>
                    )}
                  </div>
                ))}
              </div>
            </div>

            {project.attachments.length > 0 && (
              <div>
                <h4 className="font-semibold mb-2">Project Files</h4>
                <div className="space-y-2">
                  {project.attachments.map((file) => (
                    <div key={file.id} className="flex items-center justify-between p-2 bg-gray-50 rounded">
                      <div className="flex items-center gap-2">
                        <FileText className="w-4 h-4" />
                        <span className="text-sm">{file.name}</span>
                        <Badge variant="outline" className="text-xs">
                          {file.type}
                        </Badge>
                      </div>
                      <Button 
                        size="sm" 
                        variant="ghost"
                        data-testid={`button-download-${file.id}`}
                      >
                        <Download className="w-4 h-4" />
                      </Button>
                    </div>
                  ))}
                </div>
              </div>
            )}
          </div>
        )}
      </CardContent>
    </Card>
  );
}

function DocumentsSection({ projects }: { projects: Project[] }) {
  const allFiles = projects.flatMap(p => 
    p.attachments.map(a => ({ ...a, projectTitle: p.title }))
  );

  return (
    <div className="space-y-4">
      <h2 className="text-2xl font-bold">Document Library</h2>
      <div className="grid gap-4">
        {allFiles.map((file) => (
          <Card key={file.id}>
            <CardContent className="p-4">
              <div className="flex items-center justify-between">
                <div className="flex items-center gap-3">
                  <FileText className="w-6 h-6 text-blue-600" />
                  <div>
                    <p className="font-medium">{file.name}</p>
                    <p className="text-sm text-muted-foreground">From: {file.projectTitle}</p>
                  </div>
                </div>
                <div className="flex items-center gap-2">
                  <Badge variant="outline">{file.type}</Badge>
                  <Button size="sm" variant="outline" data-testid={`button-download-doc-${file.id}`}>
                    <Download className="w-4 h-4" />
                  </Button>
                </div>
              </div>
            </CardContent>
          </Card>
        ))}
      </div>
    </div>
  );
}

function CalendarSection({ projects }: { projects: Project[] }) {
  const upcomingDeadlines = projects
    .filter(p => p.deadline)
    .sort((a, b) => new Date(a.deadline!).getTime() - new Date(b.deadline!).getTime())
    .slice(0, 5);

  const upcomingMilestones = projects
    .flatMap(p => 
      p.milestones
        .filter(m => !m.completed && m.dueDate)
        .map(m => ({ ...m, projectTitle: p.title }))
    )
    .sort((a, b) => new Date(a.dueDate!).getTime() - new Date(b.dueDate!).getTime())
    .slice(0, 10);

  return (
    <div className="space-y-6">
      <h2 className="text-2xl font-bold">Upcoming Schedule</h2>

      <div className="grid md:grid-cols-2 gap-6">
        <Card>
          <CardHeader>
            <CardTitle>Project Deadlines</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="space-y-3">
              {upcomingDeadlines.map((project) => (
                <div key={project.id} className="flex items-center justify-between">
                  <div>
                    <p className="font-medium">{project.title}</p>
                    <p className="text-sm text-muted-foreground">{project.progress}% complete</p>
                  </div>
                  <Badge variant="outline">
                    {format(new Date(project.deadline!), 'MMM dd')}
                  </Badge>
                </div>
              ))}
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardHeader>
            <CardTitle>Upcoming Milestones</CardTitle>
          </CardHeader>
          <CardContent>
            <ScrollArea className="h-64">
              <div className="space-y-3">
                {upcomingMilestones.map((milestone) => (
                  <div key={milestone.id} className="flex items-start justify-between">
                    <div>
                      <p className="font-medium text-sm">{milestone.title}</p>
                      <p className="text-xs text-muted-foreground">{milestone.projectTitle}</p>
                    </div>
                    <Badge variant="outline" className="text-xs">
                      {format(new Date(milestone.dueDate!), 'MMM dd')}
                    </Badge>
                  </div>
                ))}
              </div>
            </ScrollArea>
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

server/api-routes.ts.content:
import type { Express } from "express";
import { DatabaseStorage } from "./database-storage";
import { generateChatResponse } from "./gemini";
import { getSession, requireAdminAuth, requireClientAuth } from "./auth";
import { 
  insertCmsContentSchema, 
  insertClientProjectSchema,
  insertClientSchema,
  insertAnalyticsEventSchema,
  insertChatConversationSchema 
} from "@shared/schema";
import { z } from "zod";

export function registerApiRoutes(app: Express) {
  // Setup session middleware
  app.use(getSession());

  // Use database storage
  const dbStorage = new DatabaseStorage();

  // AI Chatbot routes
  app.post("/api/chat", async (req, res) => {
    try {
      const { message, sessionId, context } = req.body;

      if (!message || !sessionId) {
        return res.status(400).json({ message: "Message and session ID are required" });
      }

      // Get conversation history for context
      const conversationHistory = await dbStorage.getChatConversations(sessionId);
      const messages = conversationHistory.map(c => ([
        { role: 'user' as const, content: c.userQuery },
        { role: 'assistant' as const, content: c.botResponse }
      ])).flat();

      // Add current message
      messages.push({ role: 'user', content: message });

      // Generate AI response
      const aiResponse = await generateChatResponse(messages, context);

      // Save conversation to database
      const conversation = await dbStorage.createChatConversation({
        sessionId,
        userQuery: message,
        botResponse: aiResponse,
        context: context || {}
      });

      res.json({ 
        response: aiResponse, 
        conversationId: conversation.id 
      });
    } catch (error) {
      console.error("Chat error:", error);
      res.status(500).json({ message: "Failed to process chat message" });
    }
  });

  app.post("/api/chat/feedback", async (req, res) => {
    try {
      const { conversationId, satisfaction } = req.body;

      if (!conversationId || !satisfaction) {
        return res.status(400).json({ message: "Conversation ID and satisfaction rating are required" });
      }

      const updatedConversation = await dbStorage.updateChatSatisfaction(conversationId, satisfaction);
      res.json({ success: true, conversation: updatedConversation });
    } catch (error) {
      console.error("Feedback error:", error);
      res.status(500).json({ message: "Failed to record feedback" });
    }
  });

  // Analytics routes
  app.post("/api/analytics/event", async (req, res) => {
    try {
      const { eventType, page, data, sessionId } = req.body;

      const event = await dbStorage.createAnalyticsEvent({
        eventType,
        page,
        data: data || {},
        sessionId,
        userAgent: req.headers['user-agent'] || '',
        ipAddress: req.ip || '',
        referrer: req.headers.referer || ''
      });

      res.json(event);
    } catch (error) {
      console.error("Analytics error:", error);
      res.status(500).json({ message: "Failed to record analytics event" });
    }
  });

  app.get("/api/analytics/summary", requireAdminAuth, async (req, res) => {
    try {
      const summary = await dbStorage.getAnalyticsSummary();
      res.json(summary);
    } catch (error) {
      console.error("Analytics summary error:", error);
      res.status(500).json({ message: "Failed to get analytics summary" });
    }
  });

  // CMS routes for admin
  app.get("/api/cms/content", requireAdminAuth, async (req, res) => {
    try {
      const content = await dbStorage.getCmsContent();
      res.json(content);
    } catch (error) {
      console.error("CMS content error:", error);
      res.status(500).json({ message: "Failed to fetch CMS content" });
    }
  });

  app.post("/api/cms/content", requireAdminAuth, async (req, res) => {
    try {
      const contentData = insertCmsContentSchema.parse(req.body);
      const content = await dbStorage.createCmsContent(contentData);
      res.json(content);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ message: "Invalid content data", errors: error.errors });
      }
      console.error("CMS creation error:", error);
      res.status(500).json({ message: "Failed to create CMS content" });
    }
  });

  app.put("/api/cms/content/:key", requireAdminAuth, async (req, res) => {
    try {
      const { key } = req.params;
      const content = await dbStorage.updateCmsContent(key, req.body);
      res.json(content);
    } catch (error) {
      console.error("CMS update error:", error);
      res.status(500).json({ message: "Failed to update CMS content" });
    }
  });

  // Client portal routes
  app.get("/api/client/projects", requireClientAuth, async (req, res) => {
    try {
      const clientId = (req as any).clientId;
      const projects = await dbStorage.getClientProjects(clientId);
      res.json(projects);
    } catch (error) {
      console.error("Client projects error:", error);
      res.status(500).json({ message: "Failed to fetch client projects" });
    }
  });

  app.get("/api/client/project/:id", requireClientAuth, async (req, res) => {
    try {
      const { id } = req.params;
      const project = await dbStorage.getClientProject(id);

      if (!project) {
        return res.status(404).json({ message: "Project not found" });
      }

      res.json(project);
    } catch (error) {
      console.error("Client project error:", error);
      res.status(500).json({ message: "Failed to fetch client project" });
    }
  });

  // Admin routes for client management
  app.get("/api/admin/clients", requireAdminAuth, async (req, res) => {
    try {
      const clients = await dbStorage.getClients();
      res.json(clients);
    } catch (error) {
      console.error("Admin clients error:", error);
      res.status(500).json({ message: "Failed to fetch clients" });
    }
  });

  app.post("/api/admin/clients", requireAdminAuth, async (req, res) => {
    try {
      const clientData = insertClientSchema.parse(req.body);
      const client = await dbStorage.createClient(clientData);
      res.json(client);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ message: "Invalid client data", errors: error.errors });
      }
      console.error("Admin client creation error:", error);
      res.status(500).json({ message: "Failed to create client" });
    }
  });

  app.get("/api/admin/projects", requireAdminAuth, async (req, res) => {
    try {
      const projects = await dbStorage.getClientProjects();
      res.json(projects);
    } catch (error) {
      console.error("Admin projects error:", error);
      res.status(500).json({ message: "Failed to fetch projects" });
    }
  });

  app.post("/api/admin/projects", requireAdminAuth, async (req, res) => {
    try {
      const projectData = insertClientProjectSchema.parse(req.body);
      const project = await dbStorage.createClientProject(projectData);
      res.json(project);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ message: "Invalid project data", errors: error.errors });
      }
      console.error("Admin project creation error:", error);
      res.status(500).json({ message: "Failed to create project" });
    }
  });

  app.put("/api/admin/projects/:id", requireAdminAuth, async (req, res) => {
    try {
      const { id } = req.params;
      const project = await dbStorage.updateClientProject(id, req.body);
      res.json(project);
    } catch (error) {
      console.error("Admin project update error:", error);
      res.status(500).json({ message: "Failed to update project" });
    }
  });
}

server/auth.ts.content:
import session from "express-session";
import connectPg from "connect-pg-simple";
import type { Express, Request, Response, NextFunction } from "express";
import jwt from "jsonwebtoken";
import bcrypt from "bcrypt";

const JWT_SECRET = process.env.JWT_SECRET || "your-super-secret-jwt-key-change-in-production";
const SESSION_SECRET = process.env.SESSION_SECRET || "your-super-secret-session-key-change-in-production";

// Session configuration
export function getSession() {
  const sessionTtl = 7 * 24 * 60 * 60 * 1000; // 1 week
  const pgStore = connectPg(session);

  const sessionStore = new pgStore({
    conString: process.env.DATABASE_URL,
    createTableIfMissing: true,
    ttl: sessionTtl,
    tableName: "sessions",
  });

  return session({
    secret: SESSION_SECRET,
    store: sessionStore,
    resave: false,
    saveUninitialized: false,
    cookie: {
      httpOnly: true,
      secure: false, // Set to true in production with HTTPS
      maxAge: sessionTtl,
    },
  });
}

// JWT token generation
export function generateToken(clientId: string): string {
  return jwt.sign({ clientId, type: 'client' }, JWT_SECRET, { expiresIn: '7d' });
}

export function generateAdminToken(userId: string): string {
  return jwt.sign({ userId, type: 'admin' }, JWT_SECRET, { expiresIn: '7d' });
}

// JWT verification
export function verifyToken(token: string): { clientId?: string; userId?: string; type: 'client' | 'admin' } | null {
  try {
    return jwt.verify(token, JWT_SECRET) as any;
  } catch {
    return null;
  }
}

// Password hashing
export async function hashPassword(password: string): Promise<string> {
  return bcrypt.hash(password, 12);
}

export async function verifyPassword(password: string, hash: string): Promise<boolean> {
  return bcrypt.compare(password, hash);
}

// Middleware for client authentication
export function requireClientAuth(req: Request, res: Response, next: NextFunction) {
  const token = req.headers.authorization?.replace('Bearer ', '') || req.session?.clientToken;

  if (!token) {
    return res.status(401).json({ message: "Authentication required" });
  }

  const decoded = verifyToken(token);
  if (!decoded || decoded.type !== 'client') {
    return res.status(401).json({ message: "Invalid or expired token" });
  }

  (req as any).clientId = decoded.clientId;
  next();
}

// Middleware for admin authentication
export function requireAdminAuth(req: Request, res: Response, next: NextFunction) {
  const token = req.headers.authorization?.replace('Bearer ', '') || req.session?.adminToken;

  if (!token) {
    return res.status(401).json({ message: "Admin authentication required" });
  }

  const decoded = verifyToken(token);
  if (!decoded || decoded.type !== 'admin') {
    return res.status(401).json({ message: "Admin access required" });
  }

  (req as any).userId = decoded.userId;
  next();
}

server/database-storage.ts.content:
import { 
  type User, 
  type InsertUser, 
  type Contact, 
  type InsertContact,
  type Newsletter,
  type InsertNewsletter,
  type PortfolioItem,
  type InsertPortfolioItem,
  type BlogPost,
  type InsertBlogPost,
  type Testimonial,
  type InsertTestimonial,
  type CmsContent,
  type InsertCmsContent,
  type ClientProject,
  type InsertClientProject,
  type Client,
  type InsertClient,
  type AnalyticsEvent,
  type InsertAnalyticsEvent,
  type ChatConversation,
  type InsertChatConversation,
  users,
  contacts,
  newsletters,
  portfolioItems,
  blogPosts,
  testimonials,
  cmsContent,
  clientProjects,
  clients,
  analyticsEvents,
  chatConversations
} from "@shared/schema";
import { db } from "./db";
import { eq, desc, and, gte, lte, sql } from "drizzle-orm";

export interface IStorage {
  // Users
  getUser(id: string): Promise<User | undefined>;
  getUserByUsername(username: string): Promise<User | undefined>;
  createUser(user: InsertUser): Promise<User>;

  // Contacts
  createContact(contact: InsertContact): Promise<Contact>;
  getContacts(): Promise<Contact[]>;

  // Newsletter
  subscribeNewsletter(newsletter: InsertNewsletter): Promise<Newsletter>;
  getNewsletterSubscribers(): Promise<Newsletter[]>;

  // Portfolio
  getPortfolioItems(): Promise<PortfolioItem[]>;
  getPortfolioItem(id: string): Promise<PortfolioItem | undefined>;
  getFeaturedPortfolioItems(): Promise<PortfolioItem[]>;
  createPortfolioItem(item: InsertPortfolioItem): Promise<PortfolioItem>;

  // Blog
  getBlogPosts(): Promise<BlogPost[]>;
  getBlogPost(slug: string): Promise<BlogPost | undefined>;
  getPublishedBlogPosts(): Promise<BlogPost[]>;
  getBlogPostsByCategory(category: string): Promise<BlogPost[]>;
  createBlogPost(post: InsertBlogPost): Promise<BlogPost>;

  // Testimonials
  getTestimonials(): Promise<Testimonial[]>;
  getFeaturedTestimonials(): Promise<Testimonial[]>;
  createTestimonial(testimonial: InsertTestimonial): Promise<Testimonial>;

  // CMS Content
  getCmsContent(): Promise<CmsContent[]>;
  getCmsContentByKey(key: string): Promise<CmsContent | undefined>;
  createCmsContent(content: InsertCmsContent): Promise<CmsContent>;
  updateCmsContent(key: string, content: Partial<InsertCmsContent>): Promise<CmsContent>;

  // Client Projects
  getClientProjects(clientId?: string): Promise<ClientProject[]>;
  getClientProject(id: string): Promise<ClientProject | undefined>;
  createClientProject(project: InsertClientProject): Promise<ClientProject>;
  updateClientProject(id: string, project: Partial<InsertClientProject>): Promise<ClientProject>;

  // Clients
  getClients(): Promise<Client[]>;
  getClient(id: string): Promise<Client | undefined>;
  getClientByEmail(email: string): Promise<Client | undefined>;
  createClient(client: InsertClient): Promise<Client>;
  updateClient(id: string, client: Partial<InsertClient>): Promise<Client>;

  // Analytics
  createAnalyticsEvent(event: InsertAnalyticsEvent): Promise<AnalyticsEvent>;
  getAnalyticsEvents(startDate?: Date, endDate?: Date): Promise<AnalyticsEvent[]>;
  getAnalyticsSummary(): Promise<{
    totalPageViews: number;
    uniqueVisitors: number;
    topPages: { page: string; views: number; }[];
    bounceRate: number;
  }>;

  // Chat
  createChatConversation(conversation: InsertChatConversation): Promise<ChatConversation>;
  getChatConversations(sessionId?: string): Promise<ChatConversation[]>;
  updateChatSatisfaction(id: string, satisfaction: number): Promise<ChatConversation>;
}

export class DatabaseStorage implements IStorage {
  // Users
  async getUser(id: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.id, id));
    return user;
  }

  async getUserByUsername(username: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.username, username));
    return user;
  }

  async createUser(insertUser: InsertUser): Promise<User> {
    const [user] = await db.insert(users).values(insertUser).returning();
    return user;
  }

  // Contacts
  async createContact(insertContact: InsertContact): Promise<Contact> {
    const [contact] = await db.insert(contacts).values(insertContact).returning();
    return contact;
  }

  async getContacts(): Promise<Contact[]> {
    return await db.select().from(contacts).orderBy(desc(contacts.createdAt));
  }

  // Newsletter
  async subscribeNewsletter(insertNewsletter: InsertNewsletter): Promise<Newsletter> {
    // Check if email already exists
    const [existing] = await db.select().from(newsletters).where(eq(newsletters.email, insertNewsletter.email));

    if (existing) {
      if (!existing.active) {
        // Reactivate subscription
        const [updated] = await db
          .update(newsletters)
          .set({ active: true })
          .where(eq(newsletters.email, insertNewsletter.email))
          .returning();
        return updated;
      }
      return existing;
    }

    const [newsletter] = await db.insert(newsletters).values(insertNewsletter).returning();
    return newsletter;
  }

  async getNewsletterSubscribers(): Promise<Newsletter[]> {
    return await db.select().from(newsletters).where(eq(newsletters.active, true));
  }

  // Portfolio
  async getPortfolioItems(): Promise<PortfolioItem[]> {
    return await db.select().from(portfolioItems).orderBy(desc(portfolioItems.createdAt));
  }

  async getPortfolioItem(id: string): Promise<PortfolioItem | undefined> {
    const [item] = await db.select().from(portfolioItems).where(eq(portfolioItems.id, id));
    return item;
  }

  async getFeaturedPortfolioItems(): Promise<PortfolioItem[]> {
    return await db.select().from(portfolioItems)
      .where(eq(portfolioItems.featured, true))
      .orderBy(desc(portfolioItems.createdAt));
  }

  async createPortfolioItem(insertItem: InsertPortfolioItem): Promise<PortfolioItem> {
    const [item] = await db.insert(portfolioItems).values(insertItem).returning();
    return item;
  }

  // Blog
  async getBlogPosts(): Promise<BlogPost[]> {
    return await db.select().from(blogPosts).orderBy(desc(blogPosts.createdAt));
  }

  async getBlogPost(slug: string): Promise<BlogPost | undefined> {
    const [post] = await db.select().from(blogPosts).where(eq(blogPosts.slug, slug));
    return post;
  }

  async getPublishedBlogPosts(): Promise<BlogPost[]> {
    return await db.select().from(blogPosts)
      .where(eq(blogPosts.published, true))
      .orderBy(desc(blogPosts.createdAt));
  }

  async getBlogPostsByCategory(category: string): Promise<BlogPost[]> {
    return await db.select().from(blogPosts)
      .where(and(eq(blogPosts.published, true), eq(blogPosts.category, category)))
      .orderBy(desc(blogPosts.createdAt));
  }

  async createBlogPost(insertPost: InsertBlogPost): Promise<BlogPost> {
    const [post] = await db.insert(blogPosts).values(insertPost).returning();
    return post;
  }

  // Testimonials
  async getTestimonials(): Promise<Testimonial[]> {
    return await db.select().from(testimonials).orderBy(desc(testimonials.createdAt));
  }

  async getFeaturedTestimonials(): Promise<Testimonial[]> {
    return await db.select().from(testimonials)
      .where(eq(testimonials.featured, true))
      .orderBy(desc(testimonials.createdAt));
  }

  async createTestimonial(insertTestimonial: InsertTestimonial): Promise<Testimonial> {
    const [testimonial] = await db.insert(testimonials).values(insertTestimonial).returning();
    return testimonial;
  }

  // CMS Content
  async getCmsContent(): Promise<CmsContent[]> {
    return await db.select().from(cmsContent).orderBy(desc(cmsContent.updatedAt));
  }

  async getCmsContentByKey(key: string): Promise<CmsContent | undefined> {
    const [content] = await db.select().from(cmsContent).where(eq(cmsContent.key, key));
    return content;
  }

  async createCmsContent(insertContent: InsertCmsContent): Promise<CmsContent> {
    const [content] = await db.insert(cmsContent).values(insertContent).returning();
    return content;
  }

  async updateCmsContent(key: string, updateContent: Partial<InsertCmsContent>): Promise<CmsContent> {
    const [content] = await db
      .update(cmsContent)
      .set({ ...updateContent, updatedAt: new Date() })
      .where(eq(cmsContent.key, key))
      .returning();
    return content;
  }

  // Client Projects
  async getClientProjects(clientId?: string): Promise<ClientProject[]> {
    if (clientId) {
      return await db.select().from(clientProjects)
        .where(eq(clientProjects.clientId, clientId))
        .orderBy(desc(clientProjects.updatedAt));
    }
    return await db.select().from(clientProjects).orderBy(desc(clientProjects.updatedAt));
  }

  async getClientProject(id: string): Promise<ClientProject | undefined> {
    const [project] = await db.select().from(clientProjects).where(eq(clientProjects.id, id));
    return project;
  }

  async createClientProject(insertProject: InsertClientProject): Promise<ClientProject> {
    const [project] = await db.insert(clientProjects).values(insertProject).returning();
    return project;
  }

  async updateClientProject(id: string, updateProject: Partial<InsertClientProject>): Promise<ClientProject> {
    const [project] = await db
      .update(clientProjects)
      .set({ ...updateProject, updatedAt: new Date() })
      .where(eq(clientProjects.id, id))
      .returning();
    return project;
  }

  // Clients
  async getClients(): Promise<Client[]> {
    return await db.select().from(clients).orderBy(desc(clients.createdAt));
  }

  async getClient(id: string): Promise<Client | undefined> {
    const [client] = await db.select().from(clients).where(eq(clients.id, id));
    return client;
  }

  async getClientByEmail(email: string): Promise<Client | undefined> {
    const [client] = await db.select().from(clients).where(eq(clients.email, email));
    return client;
  }

  async createClient(insertClient: InsertClient): Promise<Client> {
    const [client] = await db.insert(clients).values(insertClient).returning();
    return client;
  }

  async updateClient(id: string, updateClient: Partial<InsertClient>): Promise<Client> {
    const [client] = await db
      .update(clients)
      .set({ ...updateClient, updatedAt: new Date() })
      .where(eq(clients.id, id))
      .returning();
    return client;
  }

  // Analytics
  async createAnalyticsEvent(insertEvent: InsertAnalyticsEvent): Promise<AnalyticsEvent> {
    const [event] = await db.insert(analyticsEvents).values(insertEvent).returning();
    return event;
  }

  async getAnalyticsEvents(startDate?: Date, endDate?: Date): Promise<AnalyticsEvent[]> {
    if (startDate && endDate) {
      return await db.select().from(analyticsEvents)
        .where(and(
          gte(analyticsEvents.createdAt, startDate),
          lte(analyticsEvents.createdAt, endDate)
        ))
        .orderBy(desc(analyticsEvents.createdAt));
    }
    return await db.select().from(analyticsEvents).orderBy(desc(analyticsEvents.createdAt));
  }

  async getAnalyticsSummary(): Promise<{
    totalPageViews: number;
    uniqueVisitors: number;
    topPages: { page: string; views: number; }[];
    bounceRate: number;
  }> {
    // This is a simplified implementation - in production you'd want more sophisticated analytics
    const events = await db.select().from(analyticsEvents);
    const pageViews = events.filter(e => e.eventType === 'page_view');

    const uniqueVisitors = new Set(pageViews.map(e => e.sessionId)).size;
    const pageViewCounts = pageViews.reduce((acc, view) => {
      acc[view.page] = (acc[view.page] || 0) + 1;
      return acc;
    }, {} as Record<string, number>);

    const topPages = Object.entries(pageViewCounts)
      .map(([page, views]) => ({ page, views }))
      .sort((a, b) => b.views - a.views)
      .slice(0, 10);

    return {
      totalPageViews: pageViews.length,
      uniqueVisitors,
      topPages,
      bounceRate: 0.35 // Placeholder - would need more complex calculation
    };
  }

  // Chat
  async createChatConversation(insertConversation: InsertChatConversation): Promise<ChatConversation> {
    const [conversation] = await db.insert(chatConversations).values(insertConversation).returning();
    return conversation;
  }

  async getChatConversations(sessionId?: string): Promise<ChatConversation[]> {
    if (sessionId) {
      return await db.select().from(chatConversations)
        .where(eq(chatConversations.sessionId, sessionId))
        .orderBy(chatConversations.createdAt);
    }
    return await db.select().from(chatConversations).orderBy(desc(chatConversations.createdAt));
  }

  async updateChatSatisfaction(id: string, satisfaction: number): Promise<ChatConversation> {
    const [conversation] = await db
      .update(chatConversations)
      .set({ satisfaction, resolved: true })
      .where(eq(chatConversations.id, id))
      .returning();
    return conversation;
  }
}

